//first construct todo and then emit that upwards
<template>
    <div>
        <form @submit="addTodo">
            <input type="text" v-model="title" name="title" placeholder="Add Todo...">
            <input type="submit" value="Submit" class="btn">
        </form>
    </div>
</template>
<script>
import uuid from 'uuid';

export default {
    name: "AddTodo",
    data(){
        return{
            tite: ''
        }
    },
    methods: {
        addTodo(e){
            //avoid form submitting to file
            e.preventDefault();
            const newTodo = {
                id: uuid.v4(),
                title: this.title,
                completed: false
            }
            //Send up to parent
            this.$emit('add-todo', newTodo);
        }
    }
}
</script>

<style scoped>
    form{
        display: flex;
    }

    input[type="text"]{
        flex: 10;
        padding: 5px;
    }

    input[type = "submit"]{
        flex: 2;
    }

</style>
